mapper.select:
SELECT aa.role_id "roleId", aa.role_name "roleName" FROM com_role aa WHERE aa.del_flag = 'N' AND aa.role_id NOT IN (SELECT role_id "roleid" FROM com_role_user where 1=1
AND user_id = trim(#{userId})
)

SELECT a.user_id "userId", a.login_name "loginName", a.user_name "userName", a.assist_code "assistCode", a.user_type "userType", a.org_id "orgId", a.sex "sex", a.position "position", (select h.org_name from tb_usc_hospital h where a.org_id = h.org_id) "orgName", a.office_id "officeId", (select b.org_name from tb_usc_org b where a.office_id = b.org_id) "officeName", a.login_flag "loginFlag", a.email "email", a.phone_no "phoneNo", a.mobile_no "mobileNo", a.password "password", a.employee_id "employeeId", a.authorize_office_ids "authorizeOfficeIds" FROM com_user a WHERE del_flag = 'N'
AND a.user_id = #{userId}
AND lower(a.login_name) = lower(#{loginName})

SELECT	password	"password"
FROM	com_user
WHERE	user_id = #{currentUser}

mapper.update:
    	 UPDATE com_user
		 SET   mobile_no = #{mobileNo},
               phone_no = #{phoneNo}
		 WHERE user_id = #{currentUser}
